func SortUniqueDesc(numbers []int) []int {
    // Sorts a slice in descending order and removes duplicates
    seen := make(map[int]bool)
    unique := []int{}
    for _, num := range numbers {
        if !seen[num] {
            seen[num] = true
            unique = append(unique, num)
        }
    }
    sort.Sort(sort.Reverse(sort.IntSlice(unique)))
    return unique
}

func ReverseString(text string) string {
    // Reverses a string
    runes := []rune(text)
    for i, j := 0, len(runes)-1; i < j; i, j = i+1, j-1 {
        runes[i], runes[j] = runes[j], runes[i]
    }
    return string(runes)
}

func FindMaximum(numbers []int) *int {
    // Finds the maximum value in a slice
    if len(numbers) == 0 {
        return nil
    }
    max := numbers[0]
    for _, num := range numbers[1:] {
        if num > max {
            max = num
        }
    }
    return &max
}

